import React from 'react';

import { Namicorn } from 'namicorn'
const namicorn = new Namicorn()

class App extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      search: '',
      result: '',
      errorMessages: {
        UNREGISTERED_DOMAIN: 'this domain is unregistered.',
        INVALID_DOMAIN: 'this is not a valid domain.',
        NO_ADDRESSES_FOUND: 'this domain has no addresses associated with it.'
      }
    }
  }

  handleInput = (event) => {
    this.setState({
      search: event.target.value
    })
  }

  handleSubmit = async (event) => {
    event.preventDefault();
    let res = await namicorn.resolve(this.state.search)
    this.setState({
      result: res
    })
    console.log(this.state.result);

  }

  setDefault = (event) => {
    this.setState({
      search: 'mcafee2020.zil'
    })
  }

  render(){
    let content = ''
    let r = this.state.result

    switch (r) {
      case r === '' || r === null:
        break;
      case Object.keys(r.addresses).length === 0:
        content = this.state.errorMessages.NO_ADDRESSES_FOUND
        break;
      case r !== '' && r !== null:

        break
      default:
        break;
    }
    if (this.state.result !== ''  && this.state.result !== null) {

       content = Object.keys(this.state.result.addresses).map((key, index) => {
        return (<li key={index} >{key}: {this.state.result.addresses[key]}</li>)
      })
    }


    return (
      <div>
        <input type="text" onChange={this.handleInput} value={this.state.search} placeholder="enter address"/>
        <button onClick={this.handleSubmit}>submit</button>
        <button onClick={this.setDefault}>mcafee</button>
        <ul>
          {content}
        </ul>
      </div>
    )
  }
}

export default App;
