import React from 'react';

import { Namicorn } from 'namicorn'
const namicorn = new Namicorn()

class App extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      search: '',
      result: '',
      valid: false,
      errorMessages: {
        UNREGISTERED_DOMAIN: 'this domain is unregistered.',
        INVALID_DOMAIN: 'this is not a valid domain.',
        NO_ADDRESSES_FOUND: 'this domain has no addresses associated with it.'
      }
    }
  }

  S_handleInput = (event) => {
    this.setState({
      search: event.target.value
    })
  }

  S_setDefault = (event) => {
    this.setState({
      search: 'mcafee2020.zil'
    })
  }

  R_handleSubmit = async (event) => {
    let valid;
    let data;

    event.preventDefault();
    this.setState({
      valid: false
    })

    valid = await namicorn.isSupportedDomain(this.state.search)
    data = await namicorn.resolve(this.state.search)
    if (valid === true && data === null) {
      this.setState({
        result: this.state.errorMessages.UNREGISTERED_DOMAIN
      })
    }
    else if (valid === false) {
      this.setState({
        result: this.state.errorMessages.INVALID_DOMAIN
      })
    }
    else {
      this.setState({
        result: data
      })
    }

    console.log(this.state.result);
  }

  r_buildList = (content) => {
    let r = this.state.result

    if (r === false) {
      content = this.state.errorMessages.INVALID_DOMAIN
    }
    else if (r !== null && r !== '') {
      if (Object.keys(r.addresses).length === 0) {
        content = this.state.errorMessages.NO_ADDRESSES_FOUND
      }
      else {
        content = Object.keys(r.addresses).map((key, index) => {
          return (<li key={index} >{key}: {r.addresses[key]}</li>)
        })
      }
    }
    return content;
  }

  render() {
    let content = ''
    content = this.r_buildList(content)

    return (
      <div>
        <input type="text" onChange={this.S_handleInput} value={this.state.search} placeholder="enter address" />
        <button onClick={this.R_handleSubmit}>submit</button>
        <button onClick={this.S_setDefault}>mcafee</button>
        <ul>
          {content}
        </ul>
      </div>
    )
  }
}

export default App;
